import React from 'react';
import { 
  EmptyCartContainer, 
  CartLogoComponent,
  EmptyTitle,
  EmptySubtitle,
  MatchedItems,
  MatchedItem,
  ImageItem,
  TextItem,
  AddItem,
  DeleteItem
 } from './cart-container.styles';
 import ITEMS_DATA from '../../../public/items-data';

const CartContainer = ({ searchBarInput }) => {
  const filteredItems = ITEMS_DATA.items.filter(item => item.name.toLowerCase().includes(searchBarInput))

  return (
    searchBarInput.length === 0
    ?
    <EmptyCartContainer>
      <CartLogoComponent/>
      <EmptyTitle>
        Your cart is empty
      </EmptyTitle>
      <EmptySubtitle>
        Seems like you haven't chosen what to buy...
      </EmptySubtitle>
    </EmptyCartContainer>
    :
    <MatchedItems>
    {
      filteredItems.map((item, idx) => {
        const [isItemAdded, setIsItemAdded] = React.useState(false)
        return (
          <MatchedItem key={idx}>
            <ImageItem imageSrc={item.imageUrl}/>
            <TextItem> {item.name} </TextItem>
            <TextItem price> {`$${parseFloat(item.price).toFixed(2)}`} </TextItem>
            <AddItem itemAdded={isItemAdded} onClick={() => {setIsItemAdded(true)}}/>
            <DeleteItem> delete </DeleteItem>
          </MatchedItem>
        )
      })
    }
    </MatchedItems>
  )
}

export default CartContainer;
