import React from 'react';
import { SearchBarInput } from './search-bar.styles';
import ITEMS_DATA from '../../../src-public/items-data';

const handleChange = (e, setEmptyInput) => {
  setEmptyInput(e.target.value.length !== 0 ? false : true )
  console.log(ITEMS_DATA.items.filter(item => item.name.toLowerCase.includes(e.target.value)))
}

const SearchBar = ({ setEmptyInput }) => {
  return (
    <SearchBarInput
      onChange={() => handleChange(event, setEmptyInput)}
    />
  )
}

export default SearchBar;
