import React from 'react';
import { 
  EmptyCartContainer, 
  CartLogoComponent,
  EmptyTitle,
  EmptySubtitle,
  MatchedItems,
  MatchedItem
 } from './cart-container.styles';
 import ITEMS_DATA from '../../../src-public/items-data';

const CartContainer = ({ searchBarInput }) => {
  const filteredItems = ITEMS_DATA.items.filter(item => item.name.toLowerCase().includes(searchBarInput))
  console.log("filteredItems : ", filteredItems)

  return (
    searchBarInput.length === 0
    ?
    <EmptyCartContainer>
      <CartLogoComponent/>
      <EmptyTitle>
        Your cart is empty
      </EmptyTitle>
      <EmptySubtitle>
        Seems like you haven't chosen what to buy...
      </EmptySubtitle>
    </EmptyCartContainer>
    :
    <MatchedItems>
      <MatchedItem>
        <img src='/src-public/yogurt.png' style={{height:'80px', width:'74px'}}/>
        <span> GAAAA </span>
      </MatchedItem>
    </MatchedItems>
  )
}

export default CartContainer;
