import React from 'react';
import { 
  EmptyCartContainer, 
  CartLogoComponent,
  EmptyTitle,
  EmptySubtitle,
  MatchedItems,
  MatchedItem,
  ImageItem,
  TextItem,
  AddItem
 } from './cart-container.styles';
 import ITEMS_DATA from '../../../public/items-data';

const CartContainer = ({ searchBarInput, cartItems, setCartItems }) => {
  const filteredItems = ITEMS_DATA.items.filter(item => item.name.toLowerCase().includes(searchBarInput))
  console.log("cartItems : ", cartItems)

  return (
    searchBarInput.length === 0
    ?
      Object.values(cartItems).length === 0
      ?
        <EmptyCartContainer>
          <CartLogoComponent/>
          <EmptyTitle>
            Your cart is empty
          </EmptyTitle>
          <EmptySubtitle>
            Seems like you haven't chosen what to buy...
          </EmptySubtitle>
        </EmptyCartContainer>
      :
        <MatchedItems>
        {  
          Object.values(cartItems).map((item, idx) => {
            // const [isItemAdded, setIsItemAdded] = React.useState(false)
            return (
              <MatchedItem key={idx}>
                <ImageItem imageSrc={item.imageUrl}/>
                <TextItem> {item.name} </TextItem>
                <TextItem price> {`$${parseFloat(item.price).toFixed(2)}`} </TextItem>
              </MatchedItem>
            )
          })
        }
        </MatchedItems>
    :
      <MatchedItems>
      {
        filteredItems.map((item, idx) => {
          const [isItemAdded, setIsItemAdded] = React.useState(false)
          return (
            <MatchedItem key={idx}>
              <ImageItem imageSrc={item.imageUrl}/>
              <TextItem> {item.name} </TextItem>
              <TextItem price> {`$${parseFloat(item.price).toFixed(2)}`} </TextItem>
              <AddItem itemAdded={isItemAdded} onClick={() => {
                setIsItemAdded(true)
                setCartItems({
                  ...cartItems,
                  [item.name]: item
                })
              }}/>
            </MatchedItem>
          )
        })
      }
    </MatchedItems>
  )
}

export default CartContainer;
