import React from 'react';
import { SearchBarInput } from './search-bar.styles';
import ITEMS_DATA from '../../../src-public/items-data';

const handleChange = (e, setSearchBar) => {
  setSearchBar(e.target.value)
  console.log(ITEMS_DATA.items.filter(item => item.name.toLowerCase().includes(e.target.value)))
}

const SearchBar = ({ setSearchBar }) => {
  return (
    <SearchBarInput
      onChange={() => handleChange(event, setSearchBar)}
    />
  )
}

export default SearchBar;
